css:
@-moz-document regexp("https?://filmix\..*") {
html, body, #header, #main, .container-main, .page-wrapper, #content, .topmenu ul li.open, #show-more, #show-more-movie, .show-audio, .show-files, .show-more-profile, .full-panel, .panel-wrap .panel, .login.opened, .login-items, .ui-widget-overlay, .ui-dialog, .login::after, .container::before, .translations li.active, .aside-block, .aside-title {
    background-color: #012 !important;
    color: #ddd !important;
}
.topmenu, .top-date .date, .full, .full p, .fullstory, #aside, #aside > *, #aside-wrap, #aside-wrap > *, .info-b.fixed, .container, .aside-comments-item, .aside-comments-item.positive, .aside-comments-item .comments-discription, input[type="text"], form.pllist textarea, .pl-access .span, .access-items, .translations, .cr-block .cr-title, li:hover,.text_spoiler {
    background-color: #123 !important;
    color: #ccc !important;
}
.panel::before, .more .text::before, .dialog-shadow, .login-items {
    box-shadow: -2px -20px 25px #012 !important;
    -moz-box-shadow: -2px -20px 25px #012 !important;
    -webkit-box-shadow: -2px -20px 25px #012 !important;
}
#header .container, .favorites-slider, .premiere-slider, .page-wrapper, .panel-wrap .panel, .hidden-block span > *, .full.min, .upload, #pladd, input[type="file"], #main .panel-view .grid.neutral, #main .panel-view .line.neutral {
    background-color: transparent!important;
    background-image: none!important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #012), color-stop(100%, #234));
    background: none!important;
}
a, a[class], a:link, a:visited, a:hover, a:active, a:focus, h1 {
    color: #8af !important;
}
a:hover, .user-name, .comment-name, .comment-name a, .sub-nav-wrapper * > *:hover, .fatter-cat *:hover {
    color: #f26739 !important;
}
.translations li.active, .submit-btn-comment, .list-item:hover, .added-item, .added-item > *, .added-series, .added-block, .added-series > *, .added-block > *, .divForAllBtn .all-btn, .divForAllBtn .download-all, .filters, .profile-filter, .active-poster, .upload, #pladd, input[type="file"], .access-item:hover {
    background-color: #234 !important;
    color: #ccc!important;
}
aside,.sub-nav-wrapper, .sub-main-nav.col-one, #comments, .comment-text, .comment-text:hover, .replay-block, .replay-block:hover, .information, .list-container {
    background-color: #123 !important;
    color: #ddd !important;
    box-shadow: none !important;
}

/*BORDER эти два правила использовать или не использовать в паре  */
aside,.comment-text, .information, #comments, .login-items, article, .sub-nav-wrapper {
    border: 4px #345 double !important;
}
.full.min {
    max-width: 74% !important;
}
/*BORDER end*/

.series-exit, .season-block, .series-item, .series-number, .block-accordion, h2 a, h1, .name a[class], #aside .name-movie .name-rus, #main #aside .now-playing-list .list-item:hover .name-rus, .slider-item .film-name, .sub-nav-wrapper *, .fatter-cat > * {
    color: #8af !important;
}

.navigation a {
    background: #112233 !important;
    border: 3px solid #112233!important;
}
    /*Hidden  */
.page-wrapper > div:nth-child(1), .page-wrapper > div:nth-child(2),ul.share , .favorites-slider, .premiere-slider{
    display: none!important;
}

}
