css:
@-moz-document regexp('https://developer\\.apple.com/library/content/documentation/Swift/Conceptual/Swift_Programming_Language/.*') {
  .jazz #valence , .jazz #ios_header, .jazz #leave_feedback {
      display: none;
  }
  .jazz .book-parts {
      top: 0; 
      left: 0;
  }
  .jazz #bashful{
      top: 0; 
      width: calc(100% - 270px);
      margin: 0;
      margin-left: 0;
      background-color: white;
      left: 250px;
  }
  .jazz .pixel-line {
      top: 32px;
      width: calc(100% - 270px);
      left: 0;
  }
  #navigator > ul.nav-parts > li:first-child > a {
      display: none;
  }
  .jazz .chapter {
      top: 0;
      width: calc(100% - 232px);
      left: -16px;
  }
  .jazz #next_previous {
      bottom: 0;
      color: rgba(0,136,204,1);
      margin: 0 25px;
      position: absolute;
      width: calc(100% - 50px);
  }
}
