css:
@-moz-document url-prefix("https://www.intoxianime.com") {
body, #wrapper, .results #extension{
    background-color: #181818 !important;
}

#header {
	background-color: #181818
}

body *:not span, a, strong {
    color: #888 !important;
			
  }

#grid-wrapper {
	background-color: #181818
}

#nav-topbar .nav ul {
    background-color: #101010;
}

#google_center_div {
    background-color: #202020;
}

.is-scrolled .topbar-transparent #nav-topbar.desktop-sticky .nav ul {
    background-color: rgb(16, 16, 16);
}

.themeform input[type="submit"], .themeform button[type="submit"], .sidebar.s1 .sidebar-top, .sidebar.s1 .sidebar-toggle, #flexslider-featured .flex-control-nav li a.flex-active, .post-tags a:hover, .sidebar.s1 .widget_calendar caption, #footer .widget_calendar caption, .author-bio .bio-avatar:after, .commentlist li.bypostauthor > .comment-body:after, .commentlist li.comment-author-admin > .comment-body:after {
    background-color: #000000;
}

#flexslider-featured .flex-direction-nav a {
	background-color: #101010;
}

.featured, .featured.flexslider, .post-list .post-row, .post-list [class*="infinite-view-"] .post-row:last-child {
	border-bottom:none
}

.main {
    background-color: #181818 !important;
}

.col-2cl .main-inner {
	background: #181818
}

.sidebar.s1 .sidebar-top {
    background-color: #101010;
}

.entry .wp-caption, .entry table tr.alt {
    background: #424242;
}

.entry table td, .entry table th {
	border-bottom: none
}

[class="post-row"] {
	background-color: #181818
}

.col-2cl .content {
	background-color: #181818
}

[class="page-title pad group"] {
	visibility: hidden;
	display: none;
}

.nav-wrap {
	background-color: #101010;
}

.sidebar.collapsed .sidebar-content {
	background-color: #202020
}


.custom-logo-link {
	width: 270px;
    height: 100px;
	background: url(https://i.imgur.com/byGHO0A.png);
	display: block;
}

.custom-logo-link img {
	visibility: hidden;
	display: none;
}

#infinite-handle .btn-skin {
    background-color: #101010;
}

#footer-bottom {
    background: #181818;
}
}
