css:
@-moz-document url(https://trello.com/) {
	body {
		background-color: black !important;
	}
    .js-react-root {
    background-image: url("https://images.wallpaperscraft.com/image/dark_spots_texture_background_50355_1280x720.jpg");
}
.home-left-sidebar-container {
    display: none;
    padding-left: 1px;
}
.home-container {
    display: flex;
    width: 1000px;
    align-content: center;
}
.sticky-spacer {
    display: block;
    width: 1000px;
}
.home-sticky-container {
    display: flex;
    align-content: center;
    position: relative;
    width: 1000px;
}
.home-container.content-all-boards {
    width: 1000px;
    padding-left: 10px;
    margin-left: 10px;
}
.all-boards {
    display: flex;
    width: 1500px;
    align-content: center;
}
.boards-page-board-section .mod-no-sidebar {
    display: flex;
    width: 1000px;
}
.boards-page-board-section-list {
    display: flex;
    width: 1000px;
}
.header-btn .header-boards .js-boards-menu {
    display: flex;
}
.content-all-boards {
    padding-left: 0px;
}
body:not(.body-pinned-drawer) .home-container .content-all-boards {
    padding-left: 0;
}
}
