css:
@-moz-document regexp("https://twitter.com/(\\w+/status/\\d+)?") {
.dashboard-left, .dashboard-right {
    display: none;
}

#timeline {
    float: unset;
    width: 80%;
    min-width: 500px;
    max-width: 800px;
    margin-left: auto;
    margin-right: auto;
}

#page-container {
    width: unset;
}


.stream-item-header .fullname, .username, .Icon--protected, .tweet-timestamp {
    font-size: 12px !important;
    opacity:0.7;
}

.content .translate-button {
    display: none;
}

.stream-item-header {
    margin-bottom: 5px;
}

.tweet-text {
    line-height: 1.5;
    font-size: 15px;
}
}
