css:
@-moz-document domain("streamable.com") {
    * {
        color: white !important;
        border: 0px !important;
    }
    input.form-control.filter-input {
        background: transparent !important;
    }
    body {
        background-color: black;
    }
    .logo {
        filter: contrast(999%) brightness(999%) invert(100%) !important;
    }
    nav.navbar.navbar-full,
    .main-footer,
    .app-container,
    .actions-section,
    .card,
    .video-item.grid .card .card-footer,
    #video-footer,
    article#page_content,
    .page-content,
    .page-content h4,
    .page-content p,
    textarea.form-control.edit-title-input,
    .modal-content,
    .form-control .filter-input{
        background: #111 !important;
        border: 0px;
    }
    .actions-menu,
    ._1Ye1l5vAr1Tazvm0Te2dMq {
        background: #0a0a0a !important;
        border: 0px;
    }
    .main-content,
    .action-button,
    button,
    div#player,
    input,
    textarea,
    textarea.form-control.edit-title-input:active,
    textarea.form-control.edit-title-input:focus,
    textarea.form-control.edit-title-input:hover {
        background: black !important;
    }
    .loading-indicator img {
        filter: brightness(999%) grayscale(100%);
    }
}
