css:
@-moz-document url-prefix("https://tribo.me") {
.Header {
    background-color: #aa012a!important;
}
.page {
     background-image: url(https://i.imgur.com/PzEXfA7.jpg)!important;
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
        background-size: cover;
}
a {
    text-decoration: none;
    color: #c7c6c6!important;
    cursor: pointer;
}
b {
    color: #d0aaaa!important;
}
.pane {
    background: #79535f!important;
}
.Submission-Card {
    background: #5f4646!important;
}
body {
    color: #f1f1f1!important;
}
.overflow-modal {
    background: #572323!important;
}
.User-Card {
    background-color: #79535f!important;
}
textarea {
    background-color: #9a9a9a!important;
}
.Rich-Editor {
    background: #3e2525!important;
}
.Submission-Card:hover .Card-Discussion {
    background: #590312!important;
}
.Community-Card {
    background-color: #674b4b!important;
}
.Submission-Input {
    background: #c39595!important;
}
.Community-Page .community-header {
    border-bottom: 1px solid #440c0c!important;
    background-color: #600c0c!important;
}

.react-tabs__tab--selected {
    background: #a80202!important;
}
.react-tabs__tab-list {
    background-color: #851010!important;
}
.Notifications-Tray {
    background: #6a2020!important;
}

.Notification:hover {
    background-color: #ab3c3c!important;
}
.Notification {
    background-color: #824a4a!important;
}
.Notifications-Tray .header-divider {
    background-color: #91071c!important;
}
.Profile-Header {
    background-color: #7d2929!important;
}
.Wall-Post-Input {
    background: #ad3d3d!important
}
.top-attached-text {
    background-color: #6e2424!important;
}
.Activity-Card {
    background: #674b4b!important;
}
}
