css:
@-moz-document url-prefix("http://addmefast.com/") {

.piority_item,
.freepts_row_l {
    z-index: 1;
    height: 150% !important;
    color: rgb(0, 0, 0) !important;
    position: absolute !important;
    background-color: rgba(255, 255, 255, 0.5);
    width: 100%;
    left: 0px;
}
.ref-bnlock,
.righnt,
.center_subtitle,
.side-obj,
footer,
hr,
.wrapper div a img {
    display: none;
}
/* Remove Timeout Box */

.ui-widget-overlay,
.ui-dialog-content,
.ui-widget-content,
#timeout,
.ui-dialog,
.ui-widget,
.ui-widget-content,
.ui-corner-all .no-close {
    position: absolute !important;
    top: -21500px !important;
}
.header-top-items a img,
.priority_title,
.header-top-items a {
    display: block !important;
}
.actions a img {
    display: inline-block !important;
}
.add_site_item a img {
    display: inline-block !important;
}
}
