css:
@-moz-document domain("tweetdeck.twitter.com")
{

.column
, .mdl-column-rhs 
{
    width: calc(33% - 2px) !important;
}

html.dark .needs-scroll-bottom-offset.scroll-styled-h 
{ 
    bottom: 0px !important; 
}

.media-size-medium {
    min-height: 222px !important;
    background-position: top !important;
    background-size: contain !important;
} 

html.dark .thread
, .thread {
    display: none;
}

html.dark .margin-l--58
, .margin-l--58 {
    margin-left: 0px !important;
}

html.dark .margin-l---58
, .margin-l---58 {
    margin-left: 0px !important;
}

html.dark .margin-b--10
, .margin-b--10
{
    margin-left: 0px !important;
}

a.account-link
, html.dark a.account-link
{
	display: inline-block !important;
}

header.tweet-header {
	display: block !important;
}

time.tweet-timestamp {
	float: right !important;   
}


p.link-complex-target {
    float: right !important;
}

.account-inline .username {
    display: block !important;
    margin-bottom: 4px !important;
}

.tweet {
    padding-left: 0px !important;
}

.avatar {
    border-radius: unset !important;
}

header .tweet-img {
    margin-left: 0px !important;
    height: 36px !important;
    margin-right: 4px !important;
}

.quoted-tweet header .tweet-img {
    height: 0px !important;
}

.mdl {
    width:  850px;
    height: 850px;
}

.mdl .scroll-v {
    overflow-y: unset;
}

.media-preview
,a.account-link > div.nbfc
{
/*     outline: 1px solid #E1E8ED; */
/*     outline: 1px solid #14171A; */
}
}
