css:
@-moz-document domain("darty.com") {
/* Masquer certains éléments indésirables */
#home_page #vertical_slider,
#home_page #home_middle,
#home_page .darty_ecopart,
#darty_liste_produit #product_list_carousel,
#h_xxl_texte_promo,
#h_xxl_liens_annexes,
#h_xxl_topLink,
#h_xxl_services,
.product_offer
{ display: none;}



/* Réduire la hauteur du bandeau du haut */
#headerxxl .h_xxl_container #h_xxl_top {
    height: 60px;
}

#logo,
#h_xxl_logo,
#logo img {
    max-height: 50px;
    max-width: 50px;
}

#headerxxl #h_xxl_top #h_xxl_main #h_xxl_main_d .h_xxl_menu_client {
    padding-top: 0;
}
#headerxxl #h_xxl_top #h_xxl_main #h_xxl_main_d .h_xxl_menu_client #h_xxl_login #h_xxl_login_lien div, 
#headerxxl #h_xxl_top #h_xxl_main #h_xxl_main_d .h_xxl_menu_client .h_xxl_wishlist #h_xxl_wishlist_lien div, 
#headerxxl #h_xxl_top #h_xxl_main #h_xxl_main_d .h_xxl_menu_client .h_xxl_panier #h_xxl_panier_lien div {
    height: initial;
}

#h_xxl_main_g,
#h_xxl_main_d {
    max-height: 60px;
}

#main-menu, .main-menu {
    min-height: initial !important;
}
.main-menu .level-1 > li > a {
    line-height: initial;
    font-size: small;
}
.main-menu .level-2 {
    top: initial;
}

/* Réduire la hauteur du bandeau fixe du bas */
.sticky-footer-item .btn-action {
    line-height: initial;
}
}
