css:
@-moz-document regexp("^(((chrome|moz)(-extension)?|http|https):\\/\\/)?(www\\.)?(.)*\\/edit\\.html(\\?id=(.)*)?$") {
#stylish-edit {
  background-color: #1e1f17;
  color: #f8f8f2;
}
#stylish-edit h1 {
  color: #f9065f;
}
#stylish-edit input {
  background-color: #3e3d32;
  border: none;
  padding: 4px;
  border-radius: 4px;
  box-shadow: inset 1px 1px 5px 0 rgba(0, 0, 0, 0.5);
  color: #ae81ff;
}
#stylish-edit button, #stylish-edit select {
  background: linear-gradient(to bottom, #47463a 0%, #35342a 100%);
  border: none;
  padding: 4px;
  margin: 2px;
  border-radius: 4px;
  box-shadow: 1px 1px 2px 0 rgba(0, 0, 0, 0.5), inset 1px 1px 5px 0 rgba(0, 0, 0, 0.5);
  color: #ae81ff;
}
#stylish-edit h2, #stylish-edit #sections-heading {
  color: #a6e22e;
}
#stylish-edit caption {
  color: #66d9ef;
}
#stylish-edit #help-popup {
  background-color: #272822;
}
#stylish-edit .close-icon {
  background: linear-gradient(-45deg, transparent 5px, #f8f8f2 5px, #f8f8f2 6px, transparent 6.5px) no-repeat, linear-gradient(45deg, transparent 5px, #f8f8f2 5px, #f8f8f2 6px, transparent 6.5px) no-repeat;
}
#stylish-edit #sections label {
  color: #66d9ef;
}
#stylish-edit #sections .applies-to label {
  color: #f8f8f2;
}
}
