css:
@-moz-document domain("wiki-db.com") {
.alert-warning {
    background-color: #45361d;
    color: #c4b69d;
    border-color: #faebcc;
}
.alert-success {
    background-color: #1e3b1e;
    color: #9dba9e;
    border-color: #d6e9c6;
}
body {
    color: #c5c8c6;
    background-color: #282a2e;
}
button,
input,
select,
textarea {
    background: #1d1f21;
    border: transparent;
    color: #c5c8c6
}
button:hover,
input:hover {
    background: #373b41;
}
select {
    background: #1d1f21 !important;
}
select.skillitem.skillselected {
    background: #777879 !important;
    color: #1d1f21 !important;
}
.skillgenre {
    background: #373b41;
    background-color: #1d1f21 !important;
}
.panel-default {
    border-color: #373b41;
}
.panel-default > .panel-heading {
    color: #c5c8c6;
    background-color: #1d1f21;
    border-color: #373b41;
}
.panel {
    background-color: transparent;
}
.nav > li > a:focus,
.nav > li > a:hover {
    background-color: #373b41;
    color: #c5c8c6;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover {
    background-color: #1d1f21;
    color: #c5c8c6;
}
.version-label {
    background: #1d1f21;
}
#results-table tr.resultitemoverview:hover {
    background: #1d1f21;
}
.table tbody > tr.resultdetailsrow > td:hover {
    background: #1d1f21;
}
td tbody tr td {
    background: #282a2e;
}
.table-responsive th {
    background: #1d1f21;
}
.table-striped > tbody > tr:nth-child(2n+1) > td,
.table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #24262a;
}
:checked + label.borderwhenchecked,
:checked + label {
    background: #777879;
    color: #1d1f21;
}
.table-hover > tbody > tr:hover > td,
.table-hover > tbody > tr:hover > th {
    background-color: #1d1f21;
}
.breadcrumb {
	background-color: #1d1f21;
}
select optgroup {
	background-color: #141517;
}
.navbar-default .navbar-collapse, 
.navbar-default .navbar-form, 
h1 {
	background: #1d1f21;
}
.navbar-default .navbar-brand {
	color: #c5c8c6;
}
}
