css:
@-moz-document domain("eztv.ag") {

/** bar follow **/
             .forum_header_border   { position:   static  !important }


/** FB share links **/


                .at4-follow  , #novpn , .novpn_link , .header_holder { display: none  !important}


body {background-color: #111 !important;}


#header_logo {display: none !important}


#site_menu {background: #222222 !important; border: solid #222 1px !important; -moz-border-radius: 6px !important; color: #999 !important}


.forum_thread_header {background: #0c0c0c !important; color: #5a5a5a !important;}


.forum_header_border, #divider{border: solid #444 1px !important;}




    /** CHANGE COLOR OF HARCODE#3a8832 COLOR - SEEDS  **/
   .forum_thread_post * {color:#1c5600 !important}
 .forum_thread_post b, .forum_thread_post u {color:#55555b !important}
    
    
/** BACKGROUND **/

.forum_header_border tr, .forum_header_border td {background-color: #0c0c0c !important;}


/** LINK **/
    
.epinfo {color: #0a557a  !important}
    
a.thread_link {color: #1c5600 !important;}


/*IMG WHITE ! , AND DOWNLOAD MAGNET AND TORRENT*/
    
img{opacity:0.4!important;}
    
   #search_submit{opacity:0.5 !important;}
#login_submit{opacity:0.5 !important;}
.button.pumpkin.center{opacity:0.5 !important;}
    
a.download_1,a.magnet{opacity:0.4!important;}
a.download_1:hover,a.magnet:hover{opacity:0.8!important;}


/** PREVIOSELY SELECTED LINKS **/


a {

	color: #165491			!important;}


span{	background: #112		!important;}
/** GRID **/


td {

	color: #55555b			!important;

	background: #112		!important;

	border-color: #0a3564		!important;}



/** HOOVER **/


a:hover{

	color: #006600		!important;

	text-decoration: none		!important;

}
/*HOVER BACKGROUND COLOR AND ANIMATION*/
.forum_thread_post:hover {background: #071e38 !important}
.forum_thread_post:hover{animation:fading 1.5s infinite}@keyframes fading{0%{opacity:0.5}50%{opacity:1}100%{opacity:0.5}}

a.thread_link:hover {

	color: #006600			!important;}
    
}
