css:
@-moz-document domain("app.standardnotes.org") {
/* General */


.app-bar  {
  background-color: #313142 !important;
  color: white !important;
    border: 1px solid #000000 !important;
}

body {
  color: #000000 !important;
    font-family: 'Roboto' !important;
}

#save-status {
  color: #d6d6e1 !important;
}

.add-button {
  background-color: #2d2d3d !important;
}

/* Tags */

.app .tags .content {
  background-color: #191922 !important;
}

#tags-title-bar {
  color: #d8d8d8 !important;
}

.tags .tag {
  color: #d6d6e1 !important;
}

.tags .tag > .info > .title {
  color: #d6d6e1 !important;
}

.tags .tag.selected {
  background-color: #292937 !important;
}

.tags .tag:hover:not(.selected) {
  background-color: #292937 !important;
}


/* Notes */

.app .notes .content {
  background-color: #20202b !important;
}

.notes {
  border-left: 1px solid #13131a !important;
  border-right: 1px solid #13131a !important;
}


.notes .filter-section .filter-bar {
  background-color: #8d8dd !important;
}

.notes .note.selected {
  background-color: #239B56  !important;
  color: #d6d6e1 ;
}

.notes .note {
  border-bottom: 1px solid #13131a !important;
  background-color: #20202b !important;
  color: #d6d6e1;
}


/* Editor */

#editor-title-bar {
  background-color: #191922 !important;
}

#editor-title-bar > .title > .input {
  color: #d8d8d8 !important;
}

.windows-web ::-webkit-scrollbar, .windows-desktop ::-webkit-scrollbar, .linux-web ::-webkit-scrollbar, .linux-desktop ::-webkit-scrollbar {
    border-left: 0 !important;
}

.editor-content .editable {
  background-color: #202020;
  color: #d8d8d8;
  font-size: 16px !important;
  font-family: 'Roboto' !important;
}

#editor-title-bar .editor-tags .tags-input {
  color: #d8d8d8 !important;
}

#footer-bar a {
  color: #60a8f3;
}

}
