css:
body {
    font-family: "DejaVu Sans", sans-serif;
    margin: 16px;
    background-color: #21252b;
    color: #D3DAE3;
    font-size: 1.25em;
    justify-content: justify;
    text-align: justify;
    padding: 0px 80px 0px 80px;
}
pre {
    font-family: "DejaVu Sans", sans-serif;
}
dl {
    font-family: "DejaVu Sans", sans-serif;
    padding-bottom: 12px;
}
head,
h1,
title {
    font-family: "DejaVu Sans", sans-serif;
    margin: 16px;
    color: #6666AA;
    font-size: 1.7em;
    text-align: center;
    font-weight: bold;
}
img {
    display: none;
}
form {
    margin: 0.25em auto;
}
input {
    margin: 0.25em auto;
    background-color: #2B2E39;
    color: #D3DAE3;
    border-width: 0px;
    padding: 4px;
    font-family: "DejaVu Sans", sans-serif;
    font-size: 0.8em;
}
a {
    color: #A9CAF1;
    text-decoration: none;
}
a:visited {
    color: #90939b;
}
a:active {
    color: #D7787D;
}
a:hover {
    color: #D3DAE3;
    background-color: #363B48;
}
