css:
@-moz-document regexp('.*(.|\/)youtube\.com.*') {

#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-channel-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-show-renderer.ytd-grid-renderer {
    margin-bottom: 1px !important;
}

h3.ytd-grid-video-renderer {
    margin: 0px 0 0px !important;
}

.grid-subheader.ytd-shelf-renderer
{
    margin-top: 12px !important;
}

#contents.ytd-shelf-renderer
{
    margin-top: 12px !important;
}

#toggle.ytd-grid-renderer
{
    margin-bottom: 12px !important;
    margin-top: 12px !important;
}

#items.yt-horizontal-list-renderer
{
    margin-bottom: var(--ytd-horizontal-list-margin-bottom, 12px) !important;
}

.badge-style-type-collection.ytd-badge-supported-renderer
{
    padding: 0px 0 2px 0 !important;
}

ytd-watch[theater] #player.ytd-watch
{
   margin-bottom: 16px !important;
}

#player.ytd-watch
{
margin-bottom: 16px !important;
}

ytd-comment-thread-renderer
{
    margin-bottom: var(--ytd-comment-thread-margin-bottom, 4px) !important;
}

#action-buttons.ytd-comment-renderer
{
    margin-top: 0px !important;
}

ytd-comment-action-buttons-renderer[action-buttons-style="desktop-toolbar"] #reply-dialog.ytd-comment-action-buttons-renderer
{
    padding-top: 0px !important;
}

}
