css:
:root {
  --backgroundColor: #fff;
  --backgroundColor2: #d29393;
  --textColor: #2f2f2f;
}
* {
  animation: blinkbackground 2s infinite;
  color: var(--textColor) !important;
}
@-webkit-keyframes blinkbackground {
  0% {
    background-color: var(--backgroundColor);
  }
  50% {
    background-color: var(--backgroundColor2);
  }
  100% {
    background-color: var(--backgroundColor);
  }
}
