css:
@-moz-document domain("youtube.com") {
button.yt-uix-button-subscribed-branded {
  background: linear-gradient(to bottom, #fff9ce 0%,#fecb10 100%);
  color: #913a0d;
  border-width: 1px;
  border-color: #fab406;
  border-style: solid;
  border-radius: 2.5px;
}
.exp-responsive #content .yt-uix-button-subscription-container .yt-subscriber-count {
  background: linear-gradient(to bottom, #fff9ce 0%,#fecb10 100%);
  color: #913a0d;
  border-width: 1px;
  border-color: #fab406;
  border-style: solid;
  border-radius: 2.5px;
}
}
