css:
@-moz-document domain('yoraikun.wordpress.com'){
  #wrapper{
    background-color: #1a1a1a;
  }
  #main{
    background-color: #1a1a1a;
  }
  #content p {
    background-color: #1a1a1a;
    color: white;
  }
  body{
    background-color:#1a1a1a;
  }
  #site-title a{
    color: white;
  }
  .entry-title a:link, .entry-title a:visited{
    color: white;
  }
  #respond h3{
    color: white;
  }
  #site-info a{
    color:white;
  }
  .widget-title{
    color: white;
  }
  #site-description{
    color: white;
  }
  #wp-calendar caption{
    color: white;
  }
  #content .entry-title{
    color: white;
  }
  .wp-caption{
    background-color: #1a1a1a;
  }
  #content h1, #content h2, #content h3, #content h4, #content h5, #content h6{
    color: white;
  }
  h3#comments-title, h3#reply-title{
    color: white;
  }
  .comment-author cite{
    color: white;
  }
  .commentlist .bypostauthor > div{
    background-color: #1a1a1a;
  }
  
  

}
