css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rapidshare.de") {
table[cellspacing="0"][cellpadding="5"][border="2"], 
table[cellspacing="0"][cellpadding="5"][border="4"] > tbody > tr > td:first-child,
table[cellspacing="0"][cellpadding="5"][border="4"] > tbody > tr > td:first-child + td,
table[cellspacing="0"][cellpadding="5"][border="4"] > tbody > tr:not(:last-child) > td:first-child + td + td,
a[href*="premium"], a[href*="forgot"], a[href*="banners"],
#my_g_head, #my_g_first  { display: none !important }
table[cellspacing="0"][cellpadding="5"][border="4"],
td[align="center"] {border: none !important}
font { color: black !important }
}

@-moz-document url-prefix("http://rapidshare.de/files/") {
p + p { display: none !important }
}
