css:
@-moz-document domain(ThatQuiz.org) {
/* Stop annoying background tiling */
        * {
            background-size: cover !IMPORTANT
        }
        /* Quiz box and <body> (background) */
        #quiztable, body.dY4 {
            background-image: url("http://sigsmileyface.ddns.net/resources/images/backgrounds/rainbow_and_cyan.jpg")
        }
        /* Homepage & quiz boxes */
        div.fancent, td.normal {
             background-image: url("http://sigsmileyface.ddns.net/resources/images/backgrounds/clouds.jpg") !IMPORTANT
        }
        /* Sub-heading */
        div.jf7 {
            font-size: 12px;
            color: black;
        }
        /* <body> (background) of the quiz page */
        .landsc {
            background-image:url("http://sigsmileyface.ddns.net/resources/images/backgrounds/spiral_dots.jpg")
        }
        /* About us page headings */
        div.abouttitle {
            background-color: #00A0E0;
        }
}
