css:
@-moz-document regexp("https://www.ii.co.uk/discussion.*"){
div {
    color : rgb(255,255,255); 
}

/* icon that display number of click through */

.badge-notification.clicks {
    background-color: #CC2222;
    color: #fcfcfc;
}

/* @name in a discussion */

a.mention, a.mention-group {
    color: #eeeeee;
    background: #003060;
}

/* discussion updates */

.large-notification a span {
    color: #CC2222;
}

/* modal boxes */

.modal .modal-body {
    background-color: #404040;
}

.modal-footer {
    background-color: #404040;
}

.topic-list .topic-list-item-separator td span {
    background-color: #303030;
}

.nav-pills>li>a {
    color: #eeeeee;
}

#topic-title h1 a {
    color: #eeeeee;
}

.user-stream .excerpt {
    color: #eeeeee;
}

.user-stream .item, .user-stream .user-stream-item {
    background-color: #303030;
}

body {
background-color: rgb(30,30,30);
color : rgb(255,255,255); 
}

.user-main .about .details .primary {
    background-color: #303030;
}

.discourse-tag {
    background-color: #003060 !important;
    color: #eeeeee !important;
}

.topic-header-extra .discourse-tags .discourse-tag {
    color: inherit !important;
    background-color: #003060 !important;
}

.user-menu .notifications .read {
    background-color: #303030;
}

.user-menu .notifications li {
    background-color: #303030;
}

.user-menu .notifications li span {
    color: #FE0000;
}

/* menu popup */
a:visited {
    color: #eeeeee;
}

a {
    color: #eeeeee;
}

a.title, .latest-topic-list-item .main-link a.title {
    color: #eeeeee;
}

a.title {
    color: #eeeeee;
}

.topic-list-item.visited a.title:not(.badge-notification), .latest-topic-list-item.visited a.title:not(.badge-notification), .category-topic-link.visited a.title:not(.badge-notification) {
   color: #eeeeee;
}

.topic-list-main-link a.title, .topic-list .main-link a.title, .latest-topic-list-item .main-link a.title
 {
   color: #eeeeee;
}

.presence-users {
  background-color: rgb(30,30,30);
  color: #eeeeee;
 }


aside.quote .title {
    background-color: rgb(30,30,30);
    color: #eeeeee;
}

.menu-panel {
    background-color: #303030;
}

.select-kit .select-kit-row.is-highlighted {
    background: #303030;
}

.select-kit .select-kit-row {
    background: #303030;
}

.topic-map {
    background: #303030;
}

blockquote {
    background-color: #404040;
}

.highlight-strong {
    background-color: #306050;
}

.user-stream .notification.unread {
    background-color: #505050;
}

/* all categories / all tags buttons */
.list-controls .combo-box .combo-box-header {
    background: #6a46b9 !important;
    color: #eeeeee;
}

/* Create new topic */

#reply-control .reply-area {
    background-color: #303030;
}

.select-kit.combo-box .select-kit-header {
    background: #303030;
}

/* Create new topic tag box */
.select-kit.combo-box.mini-tag-chooser .select-kit-header .selected-name {
    background-color: inherit;
}

/* quoted text in the forum */
.quote aside .quote, .quote aside .title, .quote aside blockquote, .quote aside .onebox, .quote aside .onebox-result {
    background: #500050;
}

