css:
@-moz-document domain(anond.hatelabo.jp) {

div.section p {
  line-height: 196%;
}

.hatena-body {
  max-width: 600px;
}

h2 span.date {
  color: #5279e7;
}

h2 {
  color: #5279e7;
  background-color: #fff;
  padding: 0;
}

#hatena-anond div.section h3 {
  font-size: 150%;
}

div.body {
  padding: 3px 0px;
  border: none;
  font-size: 16px;
  color: #484848;
}

a.keyword {
  color: #484848;
}

div.day p {
  margin-bottom: 3%;
  margin-top: 3%;
}

@media (min-width: 600px) {
  div.body {
    font-size: 18px;
  }
}
}
