css:
@-moz-document domain("open2ch.net") {
/* 右にスペース追加 */
 body {
    margin-right: 340px !important;
    margin-bottom: 180px;
}
/* 削除依頼ボタンを消す */
 .box {
    display: none !important;
}
/* フッターを消す */
 footer {
    display: none !important;
}
/* 各種ソーシャルリンクを消す */
 ul.social {
    display: none !important;
}
/* 人数表記を中央寄せで調節 */
 .lbox {
    text-align: center;
}
/* nama放送画面を調節 */
 #nama_box {
    top: 50px;
    right: 4px !important;
}
/* kome を調節 */
 #komediv {
    margin: 2px;
    padding: 2px;
    width: 320px !important;
    right: 4px !important;
    top: 300px;
}
/* nama放送枠分予約 */
 #klog_view {
    width: auto !important;
    height: 100% !important;
    top: 300px;
}
/* ヘッダを消す */
 #kheader {
    display: none;
}
/* 緑背景の入力部分の指定 */
 #kheader ~ div {
    width: 310px !important;
    position: fixed;
    bottom: 0;
}
/* 使い方、ガイドラインを消す */
 #kmain div[align="right"] {
    display: none;
}
/* kome入力欄の調整 */
 #textTable td {
    height: 1.5em;
    position: relative;
}
#textTable div {
    float: none !important;
    position: absolute;
    top: 0;
    right: 0;
}
#kome {
    width: 250px;
    height: 100%;
}
/* 入力欄に被る広告非表示 */
.adsbygoogle {
	display: none !important;
}
}
