css:
@-moz-document url-prefix("https://www.pathofexile.com/trade/search") {
body {
    background: #000;
}

#statusBar {
    background: none;
}

#statusBar .statusBarCenter {
    background: none;
}

#statusBar .statusBarRight {
    background: none;
}

#trade {
    max-width: 1000px;
}

.results .row {
    justify-content: space-between;
    flex-wrap: wrap;
}

.results .row .left {
    width: 110px;
}

.results .row .middle {
    width: 800px;
}

.results .row .right {
    width: 100%;
    background-color: rgba(255, 255, 255, 0.1);
    margin-top: 8px;
}

.price-label {
    display: none;
}

.details {
    display: flex;
    align-items: center;
}

.results .row .s.sorted {
    background-color: transparent;
}

.results .row .s.sorted br {
    display: none;
}

.currency-text {
    padding-right: 20px;
}

.results .row .details .info {
    padding-left: 10px;
}

.results .row .details .btns {
    padding-left: 10px;
}

.results .row .copy {
    top: 8px;
    left: 46px;
}

.results .row .searchBy {
    top: 8px;
    left: 78px;
}

/* SEARCH BAR */
#trade .search-bar.search-advanced {
    padding-top: 40px;
}

#trade .controls .controls-right {
    position: absolute;
    top: 80px;
    right: 0;
}
}
