@-moz-document url("http://google.com/mail"), domain("mail.google.com") {
.z0>.L3 {
    border-radius: 2px;
    padding: 0;
    display: inline-grid;
    width: 232px;
    box-shadow: none;
    margin: 0;
    background: #e53935;
    color: #fff;
    text-transform: uppercase;
    letter-spacing: 0;
    font-size: 16px;
}
.z0>.L3::before {
    display:none;
}
.z0>.L3:hover, .z0>.L3:focus {
    box-shadow: none;
    background-color: #EF5350;
}
.bhZ:not(.bym) .z0>.L3{
    height: 48px;
    border-radius: 2px;
}
.bhZ:not(.bym) .z0>.L3::before {
    display: block;
    content:'+';
    color: #fff;
    font-size: 40px;
    line-height: 40px;
    background-image: none;
}
.TK .TO, .n6 .ah9, .CL{
    border-radius: 2px;
}
}
