css:
@-moz-document domain("bonpatron.com") {
html, #main, #content, #editor, form {
    height: 100% !important;
}

.mceLayout {
    width: 100% !important;
    height: 100% !important;
    max-height: none !important;
}

#main {
    margin: 1 !important;
    max-width: none !important;
}

#content {
    padding: 0 !important;
} 

.adsbygoogle {
    display: none !important;
}

[rowspan="2"] {
    display: none;
}

#top, #topbanner, #guidelinks {
    display: none;
}

table[style="width: 95%; margin-left: auto; margin-right: auto;"], table.bottominfo {
    display: none;
}

td[colspan="2"] {
    display: none;
}

table[cellpadding="8"] {
    margin-top: -8px ;
}
}
