css:
@-moz-document url-prefix("https://ani.gamer.com.tw/animeVideo.php?sn=") {
body {
    overflow-x:hidden;
}
.mainmenu {
    top:0;
    left: 0;
    text-align:right;
    position: absolute;
}
.sky {
    display:none;
    height: 0;
    width:0;
}
.BH_background {
    margin-top:35px;
}
@media(min-width: 1000px) {
    .BH_background {
        margin-top: -55px;
    }
}
.old_list {
    display:none;
    height: 0;
    width:0;
}
.store {
    display:none;
    height: 0;
    width:0;
}
.news_list {
    display:none;
    height: 0;
    width:0;
}
.whilebg {
    display:none;
    height: 0;
    width:0;
}
.anime-title p {
    display:none;
}
.anime_name h1 {
    font-size:2.2em;
    padding: 10px 10px 0 10px;
}
section {
    padding-top: 0;
}
.season {
    padding: 10px 10px 10px 10px;
}
@media(min-width: 1000px) {
    .anime_name h1 {
        font-size:2.2em;
        padding: 0px 0px 5px 10px;
    }
    .videoname {
        width:45%;
        float:left;
    }
    .anime-title {
        padding-top:10px;
        min-height:120px;
    }
    .season {
        width:54%;
        float:right;
    }
    .season .season-tab {
        margin-bottom: 0;
        border-bottom: none;
    }
}
.rating {
    display:none;
}
.anime_name {
    padding-left: 0px;
}
.anime_name button {
    display:none;
}
.fb_iframe_widget {
    display:none;
}
.anime-option {
    margin: 0 0 0 0;
}
.data {
    padding: 0 10px 10px 10px;
}
}
