css:
@-moz-document domain("wpbeginner.com") {
.sidebar-content .content-sidebar-wrap, .content-sidebar .content-sidebar-wrap {
    width: 100%;
    margin: 20px auto 0;
}
.sidebar-content .content, .content-sidebar .content {
    max-width: 82%;
    width: 100%;
    margin: 0 5px 5px 15px;
    background: #fff;
    border: 1px solid #dfdfdf;
}
.sidebar {
    width: 16%;
    float: left;
    margin: 0px 0 0 10px;
    display: inline;
    padding: 5px 0 0 0;
}
.site-header {
    width: 100%;
    background: #2d2520;
    height: 72px;
    margin: 0 auto 0;
}
.single .content .post p, .content .page p {
    margin: 0;
    padding: 0 0 20px 0;
    font-size: 18px;
    line-height: 1.6em;
    text-align: justify;
}
}
