css:
@-moz-document url-prefix("https://pan.baidu.com/play/video"){
/* 标题 */
.video-title
,.video-title-right{
	display:none;
}
/* header */
.header-box
{
	opacity: 0;
    position: fixed;
    width: 100%;
    z-index: 999;
}
.header-box:hover
{
	opacity:1;
}
/* video */
.video-main
{
	padding: 0!important;
    width:100%!important;
}
#video-wrap-outer,
.video-wrap,
#video-root,
.werbung-video
{
	height:100vh!important;
}
.video-wrapper,
#video-player
{
	width: 100%!important;
    height:100%!important;
}
}
