css:
@-moz-document domain(hipchat.com) {
  .aui-navgroup-inner {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
  }

  .aui-navgroup-inner > ul {
      flex: 1 0 100%;
      order: 1;
  }

  .aui-navgroup-inner > div:nth-of-type(1) {
      flex: 1 1 60%;
      order: 3;
      max-width: 60%;
  }

  .aui-navgroup-inner > div:nth-of-type(2) {
      flex: 1 1 40%;
      order: 2;
      max-width: 40%;
  }
}
