css:
@-moz-document domain("visualstudio.com"), regexp("https?://.*.visualstudio.com/?.*") {
.form-grid .tfs-collapsible-header{
	color:#000;
}

/* START HEADER */
.horizontal-layout .region-header .header-top,
.tag-box,
.tag-delete,
.tags-add-button .tag-box-selectable,
.witform-layout .work-item-form-tags .tags-items-container .bowtie-icon,
.combo-drop-popup li:hover,
.combo-drop-popup li.selected, 
.tree-picker-dropdown .tree-list-item:not(.disabled):hover {
	background-color:#000!important;
}
.horizontal-layout .region-header .header-top .tfs-selector,
.horizontal-layout .region-header .header-top .search{
    background-color: rgb(255,255,255);
    background-color: rgba(255, 255, 255, .2);
}
.horizontal-layout .region-header .header-bottom,
div.rich-content-tooltip.v2 .popup-content-container,
div.rich-content-tooltip.v2 .popup-tag::before  {
    background-color: #555;
    color: #ddd;
}
.horizontal-layout .region-header .header-bottom .commandbar-item .displayed{
    color: #aaf;
}
.tag-container {
    border-color:#000
}
.tag-box {
    /*padding: 2px 6px;
    font-size: 12px;
     color: #666; 
    background-color: #444;*/
}
.bowtie-icon {
	color:#000!important;
}
/* END HEADER */

/* START FOOTER */
.form-body{
	background:#777;
	color:#000!important;
}
/* END FOOTER */


.work-item-responsive-header .workitemcontrol .combo:not(.readonly).invalid, .work-item-responsive-header .workitemcontrol .combo:not(.readonly).focus, .work-item-responsive-header .workitemcontrol .combo:not(.readonly):focus, .work-item-responsive-header .workitemcontrol .combo:not(.readonly):hover, .work-item-responsive-header .workitemcontrol .combo:not(.readonly):hover input, .work-item-responsive-header .workitemcontrol .combo:not(.readonly) input:focus {
    background-color: transparent;
}


.capacity-control-ma table input,
.combo,
.combo>div.wrap,
.combo>div.wrap input:hover, 
.combo-drop-popup,
input:focus,
.header,
.ms-Layer, .ms-Callout-main, .ms-List-cell, .history-item-summary-text, .history-item-comment-text, div[class^="history-"], .ms-Callout-beakCurtain, .ms-Callout-beak, .ms-Tooltip-content {
    background-color:#444!important;
    color:#fff!important;
}
.vertical-tab-item {
    background-color:#444;
}
.vertical-tab-item-description div,
.vertical-tab-item-icon {
    color:#fff;
}
.vertical-tab-item.is-selected, .vertical-tab-item:hover {
	background-color:#777;
}

.tbTile.tile-dimmed,
.tbTile.tile-dimmed .tbTileContent,
#PopupContentContainer .rich-content-tooltip.popup-content-control.v2.auto-width{
	/*color:#000;*/
    background-color: #777!important;
    border-color: #444;
    opacity: 0.75;
}

#PopupContentContainer .rich-content-tooltip.popup-content-control.v2.auto-width{
}

.tbTile.tile-dimmed .tbTileContent,
.tbTile.tile-dimmed .tbTileContent .id-title-container .clickable-title,
.tbTile.tile-dimmed .tbTileContent .witRemainingWork {
    color: #fff;
    opacity: 1;
}
.tbTile.tile-dimmed .tbTileContent:hover .card-context-menu {
    background-color: #444;
    border-color: #000;
    color: #fff;
    opacity: 1;
}
.hub-content,
.hub-pivot-content,
.right-hub-content,
.taskboard-row.taskboard-content-row,
.highlight-on-row-change,
.taskboard-cell.taskboard-parent.highlight-on-row-change {
    background-color: #aaa!important;
    color: #444;
}
.taskboard-cell.ui-droppable,
.taskboard-row,
.taskboard-cell,
.taskboard-expander {
    background-color: #bbb;
    border-color: #aaa;
}
.node-content {
    color: #00f;
}
.tree-drag-tile.folder>.node-link>.node-content,
.tree-view .folder>.node-link>.node-content {
    color: #000;
}
.tree-view.bowtie-tree>ul.tree-children li.selected>a>div.node-content,
.tree-view.bowtie-tree.focus>ul.tree-children li.selected>a>div.node-content,
li.tree-drag-tile.bowtie-tree>a>div.node-content {
    background-color: #444;
    border: 1px solid #000;
    color: #aaf;
}
.workitem-dialog.ui-dialog {
    background-color: #aaa;
}
.work-item-form-main-core{
    background-color: #bbb;
}
/*.combo {
    -moz-box-shadow: inset 0 0 2px #000000;
    -webkit-box-shadow: inset 0 0 2px #000000;
    box-shadow: inset 0 0 2px #000000;
    padding-left:5px;
}*/
.richeditor-editarea body,
/*#witc_3_txt,*/
select,
.witform-layout .work-item-form-main-core .combo>div.wrap.disabled,
button, button.ui-state-default, button.ui-button,
.section-container,
.work-item-form-main-header,
.workitem-info-bar.workitem-header-bar {
	background-color:#aaa; /*was #aaa */
    color:#fff;
}
.ui-widget-overlay {
    /*filter: blur(5px);*/
    background-color: #000;
    opacity: .3;
}
}
