css:
@-moz-document domain("dominion.games")
{
  .game-page
  {
    background-image: none !important;
  }

  .game-page:after
  {
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    background-image: url(https://images3.alphacoders.com/889/889331.jpg);
    background-size: cover;
    width: 100%;
    height: 100%;
    opacity: 0.8;
    z-index: -1;
  }
}
