css:
@-moz-document domain(playbeta.pocketcasts.com) {
.content, .content div {
    background-color: #1c1c1c !important;
}

.navigation, .navigation div {
    background-color: #262626 !important;
}

.podcast-header h1, .podcast-title {
    color: #DEDEDE !important;
}

.text-content, .icon-search, .episode .title {
    color: #999999 !important;
}

.played .title, .played .date-text, .played .duration-text {
    color: #424242 !important;
}
}
