/* i really want this to be global */
.navL.navL.navL,
.navR.navR.navR {
    top:0;
    bottom:0;
    width:50%;
    font-size:0;
    color:transparent;
    background-color:transparent;
    border: none;
    padding: 0;
}
.navL.navL.navL {
    left:0;
}
.navR.navR.navR {
    right:0;
}
.navL.navL.navL:hover,
.navR.navR.navR:hover {
    background-color:transparent;
}
