css:
@-moz-document domain("gitlab.com") {
.ui-dark .breadcrumbs-links .avatar-tile,
.ui-dark .avatar-circle, 
.ui-dark .avatar, 
.ui-dark .avatar-container,
body.ui-dark .navbar-gitlab .nav > li > a.header-user-dropdown-toggle .header-user-avatar {
    border: 0;
    border-radius: 0;
}

.ui-dark .avatar,
.ui-dark .avatar-container,
.ui-dark .avatar-container img {
    background: transparent;
}

.ui-dark .navbar-gitlab {
    border-bottom: none;
}

.ui-dark .nav-sidebar {
    background-color: #2e2e2e;
    box-shadow: none;
}

.ui-dark .sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items {
    background-color: #2e2e2e;
    box-shadow: inset 1px 0 3px 0 #000;
}

.ui-dark .sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items a {
    color: #fff;
}

.ui-dark .sidebar-top-level-items > li.is-showing-fly-out .sidebar-sub-level-items > .active {
    box-shadow: inset 4px 0 0 #707070;
}

.ui-dark .nav-sidebar .context-header > a,
.ui-dark .nav-sidebar .context-header > button,
.ui-dark .nav-sidebar li a {
    color: #dfdfdf;
}

body.ui-dark .nav-sidebar li.active > a {
    color: #fff;
}

body.ui-dark .nav-sidebar li.active svg {
    fill: #fff;
}

.ui-dark .nav-sidebar .context-header > a:hover,
.ui-dark .nav-sidebar .context-header > button:hover {
    background-color: #eee;
    background-color: rgba(223, 223, 223, 0.2);
}

.ui-dark .sidebar-top-level-items > li.active {
    background-color: rgba(0, 0, 0, 0.5);
}

.ui-dark .nav-sidebar .sidebar-sub-level-items {
    padding-bottom: 0;
}

.ui-dark .nav-sidebar .sidebar-top-level-items > li.active > a:hover,
.ui-dark .nav-sidebar .sidebar-top-level-items > li > a:hover,
.ui-dark .nav-sidebar .sidebar-top-level-items > li.is-over > a,
.ui-dark .nav-sidebar .sidebar-sub-level-items > li.active a,
.ui-dark .nav-sidebar .sidebar-sub-level-items > li.active a:hover,
.ui-dark .nav-sidebar .sidebar-sub-level-items > li.active a:focus,
.ui-dark .nav-sidebar .sidebar-sub-level-items > li a:hover, 
.ui-dark .nav-sidebar .sidebar-sub-level-items > li a:focus {
    color: #fff;
    background-color: #eee;
    background-color: rgba(223, 223, 223, 0.2);
}

.ui-dark .nav-sidebar .sidebar-top-level-items > li .badge.badge-pill {
    background-color: #000;
    background-color: rgba(0, 0, 0, 0.5);
    color: #cacaca;
}

body.ui-dark .sidebar-top-level-items > li.active .badge.badge-pill {
    background-color: #2e2e2e;
    color: #cacaca;
}

.ui-dark .toggle-sidebar-button,
.ui-dark .close-nav-button {
    border-top: none;
    background-color: #2e2e2e;
}

.ui-dark .toggle-sidebar-button:hover .collapse-text,
.ui-dark .toggle-sidebar-button:hover .icon-angle-double-left, 
.ui-dark .toggle-sidebar-button:hover .icon-angle-double-right {
    color: #fff;
}

.ui-dark .nav-sidebar .dropdown-divider,
.ui-dark .nav-sidebar .divider {
    border-top: 1px solid #444;
}

}
