css:
@-moz-document domain(quay.io) {

.header-bar-element .header-bar-content {
    background: #000;
}
.co-img-bg-network {
    background: url("https://quay.io/static/img/network-tile.png") 0 0 repeat, linear-gradient(30deg, #000000, #282f33) no-repeat 0 0 fixed;
    background-color: #000000;
    color: #428bca;

}
.co-main-content-panel {
    background-color: #00000096;
}
#chatlio-widget {
    display: none;
}
.vertical cor-tabs {
    background-color: #2d2d2d;
    border-right: 1px solid #1e6eb1;
}
.co-tabs .active {
    background-color: #000;
    border-right: 1px solid #000;
}
.co-tabs li {
    border-bottom: 1px solid #1e6eb7;
}
.main-panel {
    background-color: #00000059;
    -ms-box-shadow: 0 2px 2px rgba(255, 255, 255, 0.19);
    -o-box-shadow: 0 2px 2px rgba(255, 255, 255, 0.19);
    box-shadow: 0 2px 2px rgba(255, 255, 255, 0.19);
}
.form-control {
    background-color: #000;
    border: 1px solid #003764;
}
.navbar-default .navbar-nav>li>a:focus, .navbar-default .navbar-nav>li>a:hover {
    background: #00376459;
    color: #40b4e5;
}
.btn-default.active, .btn-default.focus, .btn-default:active, .btn-default:focus, .btn-default:hover, .open>.dropdown-toggle.btn-default {
    color: #000;
    background-color: #5d5d5d;
    border-color: #428bca;
}
.btn-default {
    color: #000;
    background-color: #353535;
    border-color: #428bca;
}
.co-nav-title .co-nav-title-content {
    color: #428bca;
}
#co-l-footer {
    background-color: #000000;
}
.page-footer .copyright {
    color: #231f20;
}
.page-controls-element .btn:disabled {
    border-color: #4e4e4e;
    color: #000;
    background-color: #222;
}
.fa {
    color: #428bca;
}
.user-setup-element .setup-logo-container {
    background-color: #000000;
}
.panel-default {
    border-color: #428bca;
}
.panel {
    background-color: #000;
}


}
