css:
@-moz-document url-prefix("https://www.shanbay.com/news/articles"), url-prefix("https://www.shanbay.com/listen/review") {
.word-info > .pull-left {
    float: none;
}
.word-info > .pull-right {
    float: none;
}

i.icon-volume-off,
i.icon-volume-up,
i.ib-volume-off,
i.ib-volume-up{
    cursor: pointer;
    display: inline-block;
    height: 50px;
    width: 50px;
    text-align: center;
    line-height: 50px;
    border: .8px solid;
    border-radius: 50%;
}

i.icon-volume-off:hover,
i.icon-volume-up:hover,
i.ib-volume-off:hover,
i.ib-volume-up:hover{
    color: #fff;
    background-color: rgba(40, 190, 160, .7);
/*     background-color: rgba(172, 248, 222, .3);    */
}
}
