css:
@-moz-document url-prefix("https://dlive.io/livechat") {
body{overflow: hidden}
  
 #chat-body,.chatbody {
  background: #222222;
  overflow: hidden;
}

.has-navbar-fixed-top {
  background: #222222;
  overflow: hidden;
}

.chatroom-header,.container {
  display: none !important;
  height: 0px !important;
  overflow: hidden !important;
  margin: 0 !important;
  padding: 0 !important;
}

.navbar {
  background: #333333 !important;
  border: 0 !important;
}

.navbar-item {
  display: none !important;
}

.chatbody {
  overflow: hidden !important;
}

.chatroom {
  background-color: rgba(0, 0, 0, 0) !important;
  border: 0 !important;
  color: #ffffff;
}

.username {
  color: #00ff00 !important;
}

.color-text-primary {
  color: #ffffff !important;
}

.likeColor {
  color: #ffff00 !important;
}
}
