css:
@-moz-document url-prefix("https://dlive.io") {

.post-title, .style-extra-text, div, .user, div >span {
    color: #e7e7e7 !important;
}
  #chat-body,.chatbody
  {
    background: #222222;

  }

  .has-navbar-fixed-top
  {
    background: #222222;

  }

  .chatroom-header,.container
  {

    height: 0px !important;
    overflow: hidden !important;
    margin: 0 !important;
    padding: 0 !important;
  }

  .navbar, .chatroom-header, .header-item
  {
    background: #333333 !important;
    border-color: #222222;
     color:#e7e7e7 !important;
  }

  .navbar-item
  {

                        color:#e7e7e7;
  }

 .navbar-item:hover {
              background:#222222 !important;
          }
  .chatbody
  {
    overflow: hidden !important;
  }

  .chatroom
  {
    background-color: rgba(0, 0, 0, 0) !important;
    border: 0 !important;
    color: #ffffff;
  }

  .username
  {
    color: #00ff00 !important;
  }

  .color-text-primary
  {
    color: #ffffff !important;
  }

  .likeColor
  {
    color: #ffff00 !important;
  }

}
