css:
@-moz-document url-prefix("https://forums.opera.com/") {
body,
.header,
.topic .posts .content {
	background: #1E1E1E;
    color: #7E888C;
}
.dropdown-menu {
	background: #2B2B2B;
}
.dropdown-menu > li > a {
	color: #7E888C;
}
.categories li > .category-details,
.category > ul > li .category-details,
.well {
	background: #1E1E1E;
    color: #7E888C;
	border: 1px solid #464646;
}

.topic .post-bar .topic-main-buttons {
	background: #1E1E1E;
    color: #7E888C;
}
.categories>li .content .show-separator,
.category > ul > li .content .show-separator,
.categories>li .box-footer,
.topic .posts .content,
.navbar {
	border-color: #464646;
}
.form-control,
.tag-item {
	background: #525252;
}
.btn-default {
	background: #525252;
    color: #7E888C;
	border: none;
}
}
