css:
@-moz-document domain("mietwagen-talk.de") {
.main, .pageHeaderFacade, .pageHeaderLogo, .boxesFooterBoxes {
    background-image: url("https://i.imgur.com/3qglrNg.png");
    background-repeat: repeat;
}

.pageNavigation {
/*     background-color: rgb(50, 92, 132); */
    background-color: grey;
}

.main > .layoutBoundary, .content > .section:not(.cmsContent) {
    background-color: #4d4d4d;
    border-radius: 5px;
    color: dimgrey;
    padding-top: 5px;
    border: 0px solid darkred;
}

.boxesSidebarRight .box:not(.boxBorderless), .boxesSidebarLeft .box:not(.boxBorderless){
    background-color: darkgrey;
    border-radius: 5px;
    margin: 15px 0px;
}

section.box {
    background-color: #4d4d4d;
    color: white;
}

.contentTitle, .boxTitle, .boxTitle > a {
    color: white !important;
    font-weight: 400 !important;
}

.contentHeaderDescription, .sectionTitle {
    color: lightgrey !important;
}

.messageGroupLink, .boxesSidebarRight small {
    color: white;
}

.userLink, .wbbThreadBoardLink > a, dl.statsDataList > dt, .anchorFixedHeader, .quoteBoxIcon > .quoteBoxQuoteSymbol, .userProfilePreview, .wbbPostPreview, .messageText > p {
    color: lightgrey;
}

.tabularListRow:not(.tabularListRowHead):hover, .wbbBoardList .wbbBoard:hover, .dialogContent {
    color: lightgrey;
    background-color: dimgrey !important;
}

.sidebar dl.dataList > dt {
    color: black;
}

.new .messageGroupLink, .boxesFooterBoxes a, .pagination > ul > li:not(.skip) > a, a {
    color: lightblue;
}

a:hover, .userProfilePreview a, .icon, .popoverContent a {
    color: lightskyblue
}

[data-name] a {
    color: rgb(21, 66, 104);
}

.quoteBox {
    background-color: grey;
    color: white;
}

.userMention {
    color: black;
    background-color: lightgrey;
}

.member, .messageSidebar a:not(.userLink), .messageSidebar dl.dataList > dt, .containerList > li:hover  {
    background-color: grey;
    color: lightgrey;
    
}

.wcfLikeCounter.likeCounterLiked {
    color: lightgreen !important;
}

.wcfLikeCounter.likeCounterDisliked {
    color: #ff8080 !important;
}

.quoteBox >.toggleButton {
    background-color: lightgrey;
    color: black;
}

.userAvatarImage {
    border-radius: 5px;
}

.info {
    margin-bottom: 15px;
    border-radius: 5px;
}

.contentHeader {
    margin-top: 10px;
}

.contentFooter {
    margin-bottom: 10px;
}

.boxContainer > .box {
    padding: 7px 15px;
}

.popover {
    border-radius: 5px;
}

.popoverContent {
    background-color: #4d4d4d;
    border: 2px solid darkgrey;
    border-radius: 5px;
}

.spoilerBox > .spoilerBoxContent {
    background-color: dimgrey;
    border-radius: 3px;
}

.newMessageBadge {
    background-color: lightgreen;
    color: black;
    font-weight: bold;
}
}

@-moz-document regexp("(https:\\/\\/)?(www.)?(mietwagen-talk\\.de\\/)(user).*") {
[data-name] a {
/*     color: rgb(21, 66, 104); */
    color: lightgrey;
}

.tabMenu > ul > li > a {
    font-weight: 400;
}
}
