css:
@-moz-document domain("m-team.cc"), domain("hdcmct.org") {
img {
  max-width: 100%;
}

#mainmenu {
  padding-left: 0;
}

.mainouter {
  overflow-x: hidden;
  table-layout: fixed;
}
}

@-moz-document url-prefix("https://tp.m-team.cc/bet.php") {
td.colhead {
  background-color: #7c98ae;
}
}

@-moz-document url-prefix("https://tp.m-team.cc/forums.php") {
#outer > table:nth-child(3) > tbody > tr > td > table.main > tbody > tr > td:nth-child(1) {
  width: 90%
}

#outer > table:nth-child(3) > tbody > tr > td.embedded.nowrap,
#outer > table:nth-child(3) > tbody > tr > td > table.main > tbody > tr > td.embedded.nowrap {
  width: 10%;
  text-align: right;
}
}
