css:
@-moz-document domain("jsfiddle.net") {
body.hasHelloBar #hello-bar > .closeHb, body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > h3, body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > ul > li {
  font-size: 12px !important;
  line-height: 12px !important;
}

body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > h3, body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > ul > li {
  margin-right: 10px !important;
}

body.hasHelloBar #hello-bar {
  height: 34px !important;
}

body.hasHelloBar #hello-bar > .closeHb {
  top: 10px !important;
  right: 10px !important;
  left: initial !important;
}

body.hasHelloBar #hello-bar > .hbBody {
  padding: 10px 0 10px 58px !important;
}

body.hasHelloBar #hello-bar > .hbBody > section {
  height: 20px !important;
}

body.hasHelloBar #hello-bar > .hbBody > section:first-of-type {
  display: none !important;
}

body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > h3 {
  display: inline !important;
  color: #fff;
}

body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > ul {
  display: inline !important;
}

body.hasHelloBar #hello-bar > .hbBody > section:nth-child(2) > ul > li {
  display: inline !important;
}

body.hasHelloBar #show-result > header > h1, body.hasHelloBar #show-result > header > h1 > .changelog-badge, body.hasHelloBar #show-result > header > h1 > .changelog-badge > a > svg {
  width: 28.8px !important;
  height: 28.8px !important;
  line-height: 28.8px !important;
}

body.hasHelloBar #show-result > header > h1 {
  top: -31px !important;
}

}
