css:
@-moz-document url-prefix("https://journey.code.cool") {
.card-header,
.form-control,
.question-group,
.table,
li.list-group-item,
.list-group a {
    background-color: #323232 !important;
    color: #eee !important;
    border-color: #101010 !important;
}

.card-body,
.card {
    background-color: #202020 !important;
}

.dropdown-item:hover {
    background-color: #353535;
}

.btn-outline-secondary {
    border-color: #eee !important;
    color: #eee !important;
}

li.bg-grey,
.bg-grey > a {
    background-color: #555 !important;
}

.navbar-light,
.bg-light,
.navbar > a,
a.nav-link {
    background-color: #202020 !important;
    color: #eee !important;
    border-color: #101010 !important;
}

.dark-blue-layer,
.light-blue-layer,
div.backgroud {
    background-color: #101010 !important;
    background-image: none !important;
}

.table td,
.table th {
    border-top: none;
}

.journey-bg-success,
.journey-bg-success > td,
.journey-bg-success > th,
.table-success,
.table-success > td,
.table-success > th {
    background-color: #2E7D32 !important;
}

.journey-bg-primary,
.journey-bg-primary > td,
.journey-bg-primary > th,
.table-primary,
.table-primary > td,
.table-primary > th {
    background-color: #0277BD !important;
}

.journey-bg-warning,
.journey-bg-warning > td,
.journey-bg-warning > th,
.table-warning,
.table-warning > td,
.table-warning > th {
    background-color: #F9A825 !important;
}

.journey-bg-danger,
.journey-bg-danger > td,
.journey-bg-danger > th,
.table-danger,
.table-danger > td,
.table-danger > th {
    background-color: #D84315 !important;
}

.journey-bg-secondary,
.journey-bg-secondary > td,
.journey-bg-secondary > th,
.table-secondary,
.table-secondary > td,
.table-secondary > th {
    background-color: #424242 !important;
}

.mentor-selector {
    background-color: #101010 !important;
}

.dropdown-item {
    color: #eee !important;
}

.dropdown-item:hover {
    color: #eee !important;
    background-color: #212121;
}

span.text-muted.small {
    opacity: .8 !important;
    color: #f0f0f0 !important;
}

body {
    color: #eee;
    border-left-color: #101010 !important;
    background-color: #101010 !important;
}

i.dim-25 {
    opacity: .8 !important;
    color: #efefef !important;
}

.modal-content {
    background: #424242;
}

.calendar-cell {
    background: #212121;
}

.calendar-displayed-month {
    background-color: #424242;
}

.calendar-grid {
    background-color: #212121;
}

.select2-container--default .select2-selection--single {
    background-color: #424242;
}

.select2-dropdown {
    background-color: #424242;
}

.journey-bg-info {
    background-color: #3e5051 !important;
}

.checkbox span:before {
    font-family: "Font Awesome 5 Pro";
    font-style: normal;
    width: 1em;
    height: 1em;
    content: '\f0c8';
    margin-right: .6em;
    font-weight: lighter;
    color: #f8f9fa !important;
}

.checkbox input[type="checkbox"]:checked ~ span:before {
    content: '\f14a';
    color: #f8f9fa !important;
}
}
