css:
@-moz-document domain("wikia.com") {
.wds-global-navigation__search-toggle {
    width: 100%;
    border-bottom: solid 1px rgba(255, 255, 255, 0.5);
    padding-bottom: 5px;
    margin-left: 15px;
}

.wds-search-is-active .wds-global-navigation__search-toggle {
    width: initial;
    border-bottom: none;
    padding-bottom: initial;
    margin-left: initial;
}
}
