css:
@-moz-document domain("bing.com") {
/*搜索项调整*/

#b_content {
    padding-top:30px;
}

#b_results > .b_ans, #b_results > .b_algo {
    border-radius:6px;
    background:transparent!important;
    transition:all 1s;
    margin-bottom:12px!important;
}
#b_results > .b_ans:hover, #b_results > .b_algo:hover {
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    border-radius: 2px;
    transition:all 0.5s;
}
#b_results > .b_ans, #b_results > .b_algo, #b_context .b_ans, #b_context #wpc_ag{
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12) !important;
}

/*搜索项背景半透明*/
 #b_results > .b_ans, #b_results > .b_algo, #b_context .b_ans, #b_context #wpc_ag {
    background:rgba(255, 255, 255, 0.5)!important;
    padding:8px 14px!important;
    box-shadow:0 0 2px gray;
}
/*去广告*/
 .b_ad {
    display:none;
}
}

@-moz-document domain("baidu.com") {
#content_left > div[class^="result"] {
  display: inline-block;
  width: 100%;
  padding: 15px 15px;
  margin: 0.3em;
  word-break: normal;
  word-wrap: normal;
  position: relative;
  z-index: 1;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  border-radius: 6px;
  background-color: white;
  overflow: hidden;
}
.vOqYUu{
    display:none;
}
}
