css:
@-moz-document domain(okoun.cz) {


.item .meta {
    padding-right: 0em;
}

.head .user .login input.text {
    width: 9em;
    border: 0;
    height: 24px;
}


.welcome, .item .content .confirm.reply, .item .actions .share:after, .item.new .actions .share:after {
     background-color: transparent;
}

.room .item.new, .room .item  {
     border: 0px; 
     border-bottom: 1px solid; 
}

.head h1 a {
     background-image: none;
}

.main {
    margin-left: 10px;
}

.pager ul li, .pager ul li.older, .pager ul li.oldest, .pager ul li.newer, .pager ul li.newest, .index .news .yui-g.first .yui-u  {
    background-color: unset; 
    background-image: none;
}

.search input.text, .board .main .search input.text.authors {
    border: 0;
    height: 22px;
    padding-top: 2px;
    padding-bottom: 0px;
}

a {
    text-decoration: none;
}

.item .date, .item .permalink a, .item .meta a {
    color: #999;
}


.item .meta {
    margin-bottom: 0;
    font-size: 90%;
}

.boardlist h3 {
    font-size: 10px !important;
    margin-bottom: 0px;
    font-weight: inherit;
}

.boardlist .item {
    margin-bottom: 0px;
}

.post.content {
    /* background-color: #FFC180; */
    background-color: unset !important;
}

.user b {
    font-size: x-small;
}
  /* reklamy fuc */
  #adwrapper-lb, #adwrapper-bs {display:none !important}

  /* rozdeleni sloupcu */
.main .ctx {
    width: 20%;
    font-size: 9pt;
}
.main .main {
    width: 79%;
    font-size: 10pt !important;
}

  /* ramecek prispevku */
.listing .item {
     padding-top: 0; 
     padding-bottom: 0; 
}

h2 {
     font-family: sans-serif; 
    font-size: medium;
}

div.content {
    font-size: 100%;
}

/* popisek audirka*/
 div.descr {
        font-size: 8pt !important;
        text-align: right !important;
    position: absolute;
    right: 1%;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 49%;
    white-space: nowrap;

}


/* button s oblidenosti klubu*/
.yui-button.top {
    float: inherit;
    margin-bottom: 0.5em;
}

#body {

    margin: 0px;
    padding: 3px;
}

body {
   padding: 0;
}




}
