css:
@-moz-document url-prefix("https://userstyles.org/d/users/") {
/* add spaces */

dt, h2 {
	margin: 1em 0 0.5em 0;
    font-size: 1.8em;
}

/* add name to first list */

body:before {
	content: "Actions";
    font-size: 2em;
}

/* hide */

table, table * {
	border: none;
}

tr > td, tr > th {
	display: none;
}

/* show names */

tbody > tr > td:nth-child(1) {
	display: block;
    -webkit-margin-start: 40px;
}

/* hover effects */

a {
    position: relative;
    left: 0;
    transition: left 0.2s, opacity 0.2s;
}

a:hover {
	left: 16px;
    opacity: 1;
}

/* normalize */

a {
    font-size: 1.2em;
	text-decoration: none;
    opacity: 0.8;
}

ul {
	margin: 0;
    list-style: none;
}

}

@-moz-document url-prefix("https://userstyles.org") {
body:after {
	content: "";
}
}
