css:
@-moz-document domain("webflow.com") {
.kit-scrollbar > div:nth-child(4),
.kit-scrollbar > div:nth-child(3) > div,
div.kit-scrollbar.with-horizontal.allow-horizontal-swipe > div:last-child{
	display: none!important;
}

[width="640"]{
	padding:20px 5px!important;
}
div[viewport="[object Object]"] {
	width: 100vw!important;
	height: 100vh!important;
}

div.kit-scrollbar > div:nth-child(2){
	width: 100%!important;
	height: 90%!important;
}
.kit-scrollbar.with-horizontal.allow-horizontal-swipe,
[style="display: block;"],pre{
	width: 100%!important;
	height: 100%!important;
}
code,pre{
	user-select:all!important;
	cursor:initial!important
}
}
